INSERT INTO `bang_article_page` VALUES ('188', '【孕妇及哺乳期妇女用药】', '本品是否分泌入乳汁尚不清楚。目前尚无充分的和严格对照的孕妇研究资料。因为动物生殖试验并不总能预示人的反应，只有在充分显示治疗益处超过对胎儿的潜在危害时，方可在妊娠期使用本品。', '0', '67', '2016-12-19 14:40:34', '1');
