INSERT INTO `bang_article_page` VALUES ('253', '本品主要成份为阿维A，', '【成份】化学名称：全反式-9-（4-甲氧基-2，3，6-三甲基苯基）-3，7-二甲基-2，4，6，8-壬四烯酸。<br>化学结构式：<br><img class=\"fr-dib fr-draggable\" src=\"Uploads/Page/20161219/20161219032729.png\" style=\"width: 300px;\" data-id=\"243\"><br>分子式：C<sub>21</sub>H<sub>26</sub>O<sub>3</sub><br>分子量：326.43', '0', '71', '2016-12-19 15:27:40', '1');
