INSERT INTO `bang_article_page` VALUES ('290', '【成份】', '本品主要成份为维胺酯，<br>化学名称：N-(4-乙氧羰基苯基)维生素甲酰胺。<br>化学结构式：<br>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<img class=\"fr-dib fr-draggable\" src=\"Uploads/Page/20161220/20161220092508.png\" style=\"width: 300px;\" data-id=\"245\"><br>分子式：C<sub>29</sub>H<sub>37</sub>NO<sub>3</sub><br>分子量：447.62', '0', '72', '2016-12-20 09:25:26', '1');
