INSERT INTO `bang_article_page` VALUES ('380', '【成份】', '本品主要成份为卡泊三醇。<br>化学名称：(5Z,7E,22E,24S)-24-环丙基-9,10-开环胆甾-5,7,10(19),22-四烯-1&alpha;,3&beta;,24-三醇<br><!--[if !vml]--><!--[endif]-->化学结构式：<br>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<img class=\"fr-dib fr-draggable\" src=\"Uploads/Page/20161221/20161221103044.png\" style=\"width: 300px;\" data-id=\"254\"><br>分子式：C<sub>27</sub>H<sub>40</sub>O<sub>3</sub><br>分子量：412.61', '0', '77', '2016-12-21 10:31:00', '1');
