INSERT INTO `bang_article_page` VALUES ('402', '【成份】', '本品主要成份是他扎罗汀，<br>化学名称:6-[（3,4-二氢-4,4-二甲基-2H-1-苯并噻喃-6-基）乙炔基]-3-吡啶羧酸乙酯<br>化学结构式：<br>&nbsp; &nbsp; &nbsp; &nbsp;&nbsp;<img class=\"fr-dib fr-draggable\" src=\"Uploads/Page/20161221/20161221110559.png\" style=\"width: 300px;\" data-id=\"257\"><br>分子式：C<sub>21</sub>H<sub>21</sub>NO<sub>2</sub>S<br>分子量：351.46', '0', '78', '2016-12-21 11:05:47', '1');
