INSERT INTO `bang_article_page` VALUES ('509', '【成份】', '<p>本品主要成份为利福平，</p><p>化学名称：3-[[（4-甲基-1-哌嗪基）亚氨基]甲基]利福霉素</p><p>辅料有：氢氧化钠、甲醛合次硫酸氢钠</p><p>化学结构式：</p><p style=\"text-align:center\"><img src=\"uploads/Ueditor/20170313/1489386149278243.jpg\" title=\"1489386149278243.jpg\" alt=\".....jpg\"/></p><p>分子式：C43H58N4O12</p><p>分子量：822.95</p><p><br/></p>', '0', '83', '2016-12-22 09:53:10', '1');
