INSERT INTO `bang_article_page` VALUES ('536', '【适应症】', '与其他抗结核药联合，用于治疗各型肺结核、支气管内膜结核及肺外结核。并可作为与结核病相关手术的保护药，也可用于预防长期或大剂量皮质激素、免疫抑制治疗的结核感染及复发。', '0', '84', '2016-12-22 10:28:30', '1');
