INSERT INTO `bang_article_page` VALUES ('709', '【成份】', '本品主要成份为阿那曲唑，<br>化学名称：&alpha;,&alpha;,&alpha;&prime;,&alpha;&prime;-四甲基-5-（1H-1,2,4-三氮唑-1-甲基）-1,3-苯二乙腈。<br>化学结构式：<br>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;<img class=\"fr-dib fr-draggable\" src=\"Uploads/Page/20161223/20161223031029.png\" style=\"width: 300px;\" data-id=\"299\"><br>分子式：C<sub>17</sub>H<sub>19</sub>N<sub>5</sub><br>分子量：293.37', '0', '93', '2016-12-23 15:10:40', '1');
