INSERT INTO `bang_article_page` VALUES ('731', '【成份】', '本品主要成份为胸腺五肽<br>化学名称：N-［N-［N-［N<sup>2</sup>-L-精氨酰-L-赖氨酰］-L-&alpha;-天门冬氨酰］-L-缬氨酰］-L-酪氨酸。<br>辅料有：甘露醇<br>化学结构式：<br>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;<img class=\"fr-dib fr-draggable\" src=\"Uploads/Page/20161223/20161223035301.png\" style=\"width: 300px;\" data-id=\"302\"><br>分子式：C<sub>30</sub>H<sub>49</sub>N<sub>9</sub>O<sub>9</sub><br>分子量：679.77', '0', '94', '2016-12-23 15:53:12', '1');
